INSERT INTO `biz_project_config` VALUES (8, 21, 'SQL', 'CREATE TABLE users (\n    user_id         BIGINT UNSIGNED     AUTO_INCREMENT PRIMARY KEY COMMENT \'用户ID\',\n    username        VARCHAR(50)         NOT NULL COMMENT \'用户名\',\n    email           VARCHAR(100)        NOT NULL COMMENT \'邮箱\',\n    phone           VARCHAR(20)         COMMENT \'手机号\',\n    password_hash   VARCHAR(255)        NOT NULL COMMENT \'密码哈希\',\n    nickname        VARCHAR(50)         COMMENT \'昵称\',\n    avatar_url      VARCHAR(500)        COMMENT \'头像URL\',\n    status          TINYINT             DEFAULT 1 COMMENT \'状态: 0-禁用, 1-正常, 2-未激活\',\n    is_seller       BOOLEAN             DEFAULT FALSE COMMENT \'是否为卖家\',\n    created_at      TIMESTAMP           DEFAULT CURRENT_TIMESTAMP COMMENT \'创建时间\',\n    updated_at      TIMESTAMP           DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT \'更新时间\',\n    \n    UNIQUE KEY uk_email (email),\n    UNIQUE KEY uk_phone (phone),\n    INDEX idx_username (username),\n    INDEX idx_status_created (status, created_at)\n) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT=\'用户表\';\n\nCREATE TABLE addresses (\n    address_id      BIGINT UNSIGNED     AUTO_INCREMENT PRIMARY KEY COMMENT \'地址ID\',\n    user_id         BIGINT UNSIGNED     NOT NULL COMMENT \'用户ID\',\n    receiver_name   VARCHAR(50)         NOT NULL COMMENT \'收件人姓名\',\n    phone           VARCHAR(20)         NOT NULL COMMENT \'联系电话\',\n    province        VARCHAR(50)         NOT NULL COMMENT \'省份\',\n    city            VARCHAR(50)         NOT NULL COMMENT \'城市\',\n    district        VARCHAR(50)         NOT NULL COMMENT \'区县\',\n    detail_address  VARCHAR(200)        NOT NULL COMMENT \'详细地址\',\n    zip_code        VARCHAR(10)         COMMENT \'邮编\',\n    is_default      BOOLEAN             DEFAULT FALSE COMMENT \'是否默认地址\',\n    created_at      TIMESTAMP           DEFAULT CURRENT_TIMESTAMP COMMENT \'创建时间\',\n    \n    FOREIGN KEY (user_id) REFERENCES users(user_id) ON DELETE CASCADE,\n    INDEX idx_user_default (user_id, is_default),\n    INDEX idx_user_created (user_id, created_at)\n) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT=\'用户收货地址表\';\n\nCREATE TABLE categories (\n    category_id     INT UNSIGNED        AUTO_INCREMENT PRIMARY KEY COMMENT \'分类ID\',\n    parent_id       INT UNSIGNED        DEFAULT 0 COMMENT \'父分类ID, 0为顶级分类\',\n    name            VARCHAR(50)         NOT NULL COMMENT \'分类名称\',\n    level           TINYINT             DEFAULT 1 COMMENT \'层级: 1-一级, 2-二级, 3-三级\',\n    sort_order      INT                 DEFAULT 0 COMMENT \'排序号\',\n    icon_url        VARCHAR(500)        COMMENT \'图标URL\',\n    status          TINYINT             DEFAULT 1 COMMENT \'状态: 0-禁用, 1-启用\',\n    created_at      TIMESTAMP           DEFAULT CURRENT_TIMESTAMP COMMENT \'创建时间\',\n    \n    INDEX idx_parent_level (parent_id, level),\n    INDEX idx_status_sort (status, sort_order)\n) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT=\'商品分类表\';\n\nCREATE TABLE products (\n    product_id      BIGINT UNSIGNED     AUTO_INCREMENT PRIMARY KEY COMMENT \'商品ID\',\n    seller_id       BIGINT UNSIGNED     NOT NULL COMMENT \'卖家用户ID\',\n    category_id     INT UNSIGNED        NOT NULL COMMENT \'分类ID\',\n    name            VARCHAR(200)        NOT NULL COMMENT \'商品名称\',\n    description     TEXT                COMMENT \'商品描述\',\n    main_image      VARCHAR(500)        COMMENT \'主图URL\',\n    price           DECIMAL(12, 2)      NOT NULL COMMENT \'售价\',\n    original_price  DECIMAL(12, 2)      COMMENT \'原价\',\n    stock_quantity  INT UNSIGNED        DEFAULT 0 COMMENT \'库存数量\',\n    sales_count     INT UNSIGNED        DEFAULT 0 COMMENT \'销量\',\n    status          TINYINT             DEFAULT 0 COMMENT \'状态: 0-下架, 1-上架, 2-删除\',\n    created_at      TIMESTAMP           DEFAULT CURRENT_TIMESTAMP COMMENT \'创建时间\',\n    updated_at      TIMESTAMP           DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT \'更新时间\',\n    \n    FOREIGN KEY (seller_id) REFERENCES users(user_id),\n    FOREIGN KEY (category_id) REFERENCES categories(category_id),\n    INDEX idx_seller_status (seller_id, status),\n    INDEX idx_category_status (category_id, status),\n    INDEX idx_price (price),\n    INDEX idx_status_sales (status, sales_count),\n    FULLTEXT INDEX ft_name (name)\n) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT=\'商品表\';\n\nCREATE TABLE product_images (\n    image_id        BIGINT UNSIGNED     AUTO_INCREMENT PRIMARY KEY COMMENT \'图片ID\',\n    product_id      BIGINT UNSIGNED     NOT NULL COMMENT \'商品ID\',\n    image_url       VARCHAR(500)        NOT NULL COMMENT \'图片URL\',\n    sort_order      INT                 DEFAULT 0 COMMENT \'排序号\',\n    is_main         BOOLEAN             DEFAULT FALSE COMMENT \'是否主图\',\n    created_at      TIMESTAMP           DEFAULT CURRENT_TIMESTAMP COMMENT \'创建时间\',\n    \n    FOREIGN KEY (product_id) REFERENCES products(product_id) ON DELETE CASCADE,\n    INDEX idx_product_sort (product_id, sort_order)\n) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT=\'商品图片表\';\n\nCREATE TABLE orders (\n    order_id        BIGINT UNSIGNED     AUTO_INCREMENT PRIMARY KEY COMMENT \'订单ID\',\n    order_no        VARCHAR(32)         NOT NULL COMMENT \'订单编号\',\n    buyer_id        BIGINT UNSIGNED     NOT NULL COMMENT \'买家用户ID\',\n    address_id      BIGINT UNSIGNED     NOT NULL COMMENT \'收货地址ID\',\n    total_amount    DECIMAL(12, 2)      NOT NULL COMMENT \'订单总金额\',\n    discount_amount DECIMAL(12, 2)      DEFAULT 0.00 COMMENT \'优惠金额\',\n    freight_amount  DECIMAL(10, 2)      DEFAULT 0.00 COMMENT \'运费\',\n    pay_amount      DECIMAL(12, 2)      NOT NULL COMMENT \'应付金额\',\n    status          TINYINT             DEFAULT 0 COMMENT \'状态: 0-待付款, 1-已付款, 2-已发货, 3-已完成, 4-已取消, 5-退款中, 6-已退款\',\n    remark          VARCHAR(500)        COMMENT \'买家备注\',\n    paid_at         TIMESTAMP           COMMENT \'支付时间\',\n    shipped_at      TIMESTAMP           COMMENT \'发货时间\',\n    completed_at    TIMESTAMP           COMMENT \'完成时间\',\n    created_at      TIMESTAMP           DEFAULT CURRENT_TIMESTAMP COMMENT \'创建时间\',\n    updated_at      TIMESTAMP           DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT \'更新时间\',\n    \n    FOREIGN KEY (buyer_id) REFERENCES users(user_id),\n    FOREIGN KEY (address_id) REFERENCES addresses(address_id),\n    UNIQUE KEY uk_order_no (order_no),\n    INDEX idx_buyer_status (buyer_id, status),\n    INDEX idx_status_created (status, created_at),\n    INDEX idx_paid_at (paid_at)\n) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT=\'订单表\';', NULL, NULL, NULL, NULL, NULL, '2026-02-14 13:18:10');
